        geom_text(data = tibble(label = " 0.5%",
                                x = 1.6, y = .09),
                  mapping = aes(x = x, y = y, label = label),
                  fontface = "plain", hjust = .5, size = 2.9, color = "#333333", family = "Calibri") +
        # scale_shape_manual(values = chart_data_shape_list) +
