                                       str_detect(string = hs_product_code, pattern = regex("^2707")) ~ "Coal",
                                       str_detect(string = hs_product_code, pattern = regex("^2708")) ~ "Coal",
                                       str_detect(string = hs_product_code, pattern = regex("^2709")) ~ "Crude oil",
